<script>
import getData from "@/pages/trade/navbar/components/data.json";
import HoSoUserInfo from "@/pages/trade/slidebar/HoSoUser";
import I18n from "./i18n";
import AuthenticationService from "@/services/AuthenticationService";
import VuePerfectScrollbar from "vue-perfect-scrollbar";
import HisOrderBet from '@/pages/trade/slidebar/HisOrderBet.vue';

export default {
  components: {
    I18n,
    HoSoUserInfo,
    VuePerfectScrollbar,
    HisOrderBet
  },
  data() {
    return {
      blObj: getData,
      switchAmThanh: true,
      popupActiveCaiDat: false,
      addSidebarHisOrder: false,
      // Data Sidebar
      addNewDataSidebar: false,
      sidebarData: {},
      popupActiveNapNhanh: false,
      balanceUSDT: 0,
      enterMoneyDeposit: "",
      walletIsSelect: "usdt",
      showPopWalSL: false,
      ttNhan: 0.0,
      settings: {
        // perfectscrollbar settings
        maxScrollbarLength: 60,
        wheelSpeed: 0.6,
      },
    };
  },

  methods: {
    logOut() {
      localStorage.removeItem("tokenUser");
      localStorage.removeItem("INFO");
      // xa notice = 0
      getData.Notify = 0;
      localStorage.removeItem("stateOpen");
      // This is just for demo Purpose. If user clicks on logout -> redirect
      //this.$router.push('/login').catch(() => {})
      window.location.href = window.location.origin + "/login";
    },

    viewHoSo() {
      this.toggleDataSidebar(true);
    },

    toggleDataSidebar(val = false) {
      this.addNewDataSidebar = val;
    },

    getBalanceWalletClick() {
      AuthenticationService.getBalanceWallet().then((res) => {
        if (res.data.success) {
          let mU = res.data.data.usdt;
          this.balanceUSDT = this.formatPrice(mU, 2);
        }
      });
    },

    selectWallet(val) {
      this.walletIsSelect = val;
      this.showPopWalSL = false;
    },

    isNumber(value) {
      return typeof value === "number" && isFinite(value);
    },

    formatPrice(value, minimum) {
      var formatter = new Intl.NumberFormat("en-US", {
        //style: 'currency',
        //currency: '',
        minimumFractionDigits: minimum,
      });
      return formatter.format(value);
    },

    replaceAll(str, find, replace) {
      return Number(str.replace(new RegExp(find, "g"), replace));
    },

    ttThucNhan(e) {
      let amount = e.currentTarget.value;
      amount = this.replaceAll(amount, ",", "");
      amount = this.replaceAll(amount.toString(), "-", "");

      if (!this.isNumber(amount) || amount <= 0) {
        return this.$vs.notify({
          text: "Invalid value",
          color: "danger",
          position: "top-right",
          iconPack: "feather",
          icon: "icon-x-circle",
        });
      }

      this.ttNhan = this.formatPrice(amount, 2);
    },

    submitDeposit() {
      let amount = this.ttNhan;

      amount = this.replaceAll(amount.toString(), ",", "");
      amount = this.replaceAll(amount.toString(), "-", "");

      if (amount <= 0) {
        return this.$vs.notify({
          text: "Invalid value",
          color: "danger",
          position: "top-right",
          iconPack: "feather",
          icon: "icon-x-circle",
        });
      }

      if (amount < 11) {
        return this.$vs.notify({
          text: "The minimum amount is not enough",
          color: "danger",
          position: "top-right",
          iconPack: "feather",
          icon: "icon-x-circle",
        });
      }

      let obj = {
        money: amount,
        id: getData.uidLive,
      };

      AuthenticationService.depositWallet(obj).then((res) => {
        if (res.data.success == 1) {
          getData.balance -= amount;
          getData.blLive += amount;

          this.$vs.notify({
            text: "Recharge successful.",
            iconPack: "feather",
            icon: "icon-check",
            color: "success",
          });
        } else if (res.data.success == 3) {
          this.$vs.notify({
            text: "Your balance is not enough.",
            color: "danger",
            position: "top-right",
            iconPack: "feather",
            icon: "icon-x-circle",
          });
        } else {
          this.$vs.notify({
            text: "Invalid value",
            color: "danger",
            position: "top-right",
            iconPack: "feather",
            icon: "icon-x-circle",
          });
        }
      });
    },

    allEnterWallet() {
      this.ttNhan = this.formatPrice(getData.balance, 2);
      this.enterMoneyDeposit = this.formatPrice(getData.balance, 2);
    },

    OnOffSound() {
      if (this.switchAmThanh) {
        localStorage.setItem("SOUND", true);
      } else {
        localStorage.setItem("SOUND", false);
      }
    },
    toggleDataSidebarHisOrder() {
      this.$store.commit("TOGGLE_IS_OPEN_SIDEBAR");
    },
  },
};
</script>

