<script>
import moment from "moment";
import config from '@/config.json';

export default {
  props: {
    champion: {}
  },
  data() {
    return {
      tabActive: 0,
      reward: this.champion.totalRewards.split(','),
      day: 0,
      hour: 0,
      min: 0,
      sec: 0,
      interval: null,
      imagesRank: [
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,",
        "data:image/svg+xml;base64,"
      ],
      dm: config.domain,
      isShowTime: true,
    };
  },
  methods: {
    changeTabActive(tabActive) {
      this.tabActive = tabActive;
    },
    formatUSD(usd) {
      var formatter = new Intl.NumberFormat("en-US", {
        style: "currency",
        currency: "USD"

        // These options are needed to round to whole numbers if that's what you want.
        //minimumFractionDigits: 0, // (this suffices for whole numbers, but will print 2500.10 as $2,500.1)
        //maximumFractionDigits: 0, // (causes 2500.99 to be printed as $2,501)
      });
      return formatter.format(usd);
    },
    onBack() {
      this.$emit("goBack");
    },
    updateChampion() {
      this.$emit('update');
    }
  },
  computed: {
    bg() {
      return this.dm + 'api/auth/photo/champions/' + this.champion.background;
    }
  },
  filters: {
    getTypeChampion(val) {
      switch (val) {
        case "klgd":
          return "KLGD";

        case "hh":
          return "HOA HỒNG";
        default:
          break;
      }
    },
    formatDate(date) {
      return moment(date).format("DD/MM/YYYY hh:mm:ss");
    }
  },
  mounted() {
    this.interval = setInterval(() => {
      const d = moment.duration(moment(this.champion.dateEnd).diff(moment()));
      if (d.milliseconds() < 0) {
        this.isShowTime = false;
        clearInterval(this.interval);
      }
      this.day = d.days();
      this.hour = d.hours();
      this.min = d.minutes();
      this.sec = d.seconds();
    }, 1000);
  },
  beforeDestroy() {
    clearInterval(this.interval);
  }
};
</script>

