tua***@gmail.com`,
        },
      ],
      itemsPerPage: 20,
      hour: 0,
      min: 0,
      sec: 0,
      klgdPerDay: 0,
      domainRealName: config.domainRealName,
    };
  },
  methods: {
    formatDate(value) {
      if (value) {
        return moment(String(value)).format("DD/MM/YYYY");
      }
    },
  },
  mounted() {
    this.interval = setInterval(() => {
      const d = moment.duration(moment().endOf("day").diff(moment()));
      if (d.milliseconds() < 0) {
        this.isShowTime = false;
        clearInterval(this.interval);
      }
      this.hour = d.hours();
      this.min = d.minutes();
      this.sec = d.seconds();
    }, 1000);

    AuthenticationService.getLuckyDraw().then((resp) => {
      this.luckyDrawFake = resp.data.data;
    });
    AuthenticationService.getThongTinLoiNhuanHangNgay().then((resp) => {
      this.klgdPerDay = resp.data.data.win + resp.data.data.lose;
    });
  },
  beforeDestroy() {
    clearInterval(this.interval);
  },
  computed: {
    luckyDraw() {
      return this.luckyDrawFake;
    },
  },
};
</script>

